 79→/// Test package(s) with a translation spec
 80→#[derive(Args)]
 81→pub struct TestCmd {
 82→    /// Package to test (name or path, e.g. "." for current dir)
 83→    #[arg(value_name = "PACKAGE")]
 84→    pub positional: Option<String>,
 85→    /// Package to test (defaults to current directory or all packages)
 86→    #[arg(short = 'p', long = "package")]
 87→    pub package: Option<String>,
 88→    /// Test all workspace packages (even when in a package directory)
 89→    #[arg(short = 'w', long = "workspace")]
 90→    pub workspace: bool,
 91→    /// Spec file(s) or glob pattern(s) to test with (defaults to package's tspec file)
 92→    #[arg(short = 't', long = "tspec", num_args = 1..)]
 93→    pub tspec: Vec<String>,
 94→    /// Release build
 95→    #[arg(short, long, conflicts_with = "profile")]
 96→    pub release: bool,
 97→    /// Build profile (e.g., release, release-small, or any custom profile)
 98→    #[arg(long)]
 99→    pub profile: Option<String>,
100→    /// Stop on first failure
101→    #[arg(short, long)]
102→    pub fail_fast: bool,
103→    /// List test targets and test functions
104→    #[arg(short, long)]
105→    pub list: bool,
106→    /// List available target names for use with --test
107→    #[arg(long = "target-names")]
108→    pub target_names: bool,
109→    /// Filter by qualified test name, e.g. '-n _by_' matches 'ts_cmd::remove::tests::remove_by_index'
110→    #[arg(short = 'n', long = "names", num_args = 1..)]
111→    pub name_filter: Vec<String>,
112→    /// Run only the named test target (use --target-names to see available names; repeatable)
113→    #[arg(long = "test", value_name = "TARGET-NAME")]
114→    pub test_target: Vec<String>,
115→    /// Include #[ignore]'d tests (equivalent to -- --include-ignored)
116→    #[arg(long = "all-tests")]
117→    pub all_tests: bool,
118→    /// Extra arguments passed after -- to the test binary (e.g., --ignored)
119→    #[arg(last = true)]
120→    pub test_args: Vec<String>,
121→}
122→
123→impl TestCmd {
124→    fn effective_profile(&self) -> Option<&str> {
125→        if let Some(ref p) = self.profile {
126→            Some(p)
127→        } else if self.release {
128→            Some("release")
129→        } else {
130→            None
131→        }
132→    }
133→}
134→
135→impl Execute for TestCmd {
136→    fn execute(&self, project_root: &Path, flags: &CargoFlags) -> Result<ExitCode> {
137→        let cli_profile = self.effective_profile();
138→
139→        // Resolve package: --workspace > -p/positional PKG > cwd > all
140→        let resolved = if self.workspace {
141→            None
142→        } else {
143→            match self.positional.as_deref().or(self.package.as_deref()) {
144→                Some(pkg) => resolve_package_arg(pkg)?,
145→                None => current_package_name(),
146→            }
147→        };
148→
149→        if self.target_names {
150→            return list_target_names(resolved.as_deref(), flags);
151→        }
152→
153→        if self.list {
154→            return list_tests(resolved.as_deref(), &self.name_filter, flags);
155→        }
156→
157→        // Build extra_args from --names, --test, --all-tests, and trailing args
158→        let has_extra = !self.name_filter.is_empty()
159→            || !self.test_target.is_empty()
160→            || !self.test_args.is_empty()
161→            || self.all_tests;
162→        let flags = if has_extra {
163→            let mut f = flags.clone();
164→            for target in &self.test_target {
165→                f.extra_args.push("--test".to_string());
166→                f.extra_args.push(target.clone());
167→            }
168→            // Name filters, --all-tests, and test_args all go after --
169→            // The test harness accepts [FILTERS...] as OR-matched substrings
170→            if !self.name_filter.is_empty() || !self.test_args.is_empty() || self.all_tests {
171→                f.extra_args.push("--".to_string());
172→                f.extra_args.extend(self.name_filter.clone());
173→                if self.all_tests {
174→                    f.extra_args.push("--include-ignored".to_string());
175→                }
176→                f.extra_args.extend(self.test_args.clone());
177→            }
178→            f
179→        } else {
180→            flags.clone()
181→        };
182→
183→        match resolved {
184→            None => {
185→                let workspace = WorkspaceInfo::discover()?;
186→
187→                // When --test targets are specified in workspace mode, only test
188→                // packages that actually have matching files in tests/
189→                if !self.test_target.is_empty() {
190→                    let members = workspace.buildable_members();
191→                    let matching: Vec<_> = members
192→                        .iter()
193→                        .filter(|m| {
194→                            self.test_target
195→                                .iter()
196→                                .any(|t| m.path.join("tests").join(format!("{t}.rs")).exists())
197→                        })
198→                        .collect();
199→
200→                    if matching.is_empty() {
201→                        eprintln!(
202→                            "No packages contain test targets: {}",
203→                            self.test_target.join(", ")
204→                        );
205→                        return Ok(ExitCode::from(1));
206→                    }
207→
208→                    for member in &matching {
209→                        println!("=== {} ===", member.name);
210→                        test_package(&member.name, None, cli_profile, &flags)?;
211→                    }
212→                    return Ok(ExitCode::SUCCESS);
213→                }
214→
215→                let results =
216→                    test_all(&workspace, &self.tspec, cli_profile, self.fail_fast, &flags);
217→                Ok(print_test_summary(workspace.name(), &results))
218→            }
219→            Some(name) => {
220→                let mut all_lines = Vec::new();
221→                if self.tspec.is_empty() {
222→                    all_lines = test_package(&name, None, cli_profile, &flags)?;
223→                } else {
224→                    let package_dir = resolve_package_dir(project_root, Some(&name))?;
225→                    let pkg_name = get_package_name(&package_dir)?;
226→                    let spec_paths = find_tspecs(&package_dir, &self.tspec)?;
227→                    for spec_path in &spec_paths {
228→                        let spec_str = spec_path.to_string_lossy();
229→                        let lines = test_package(&pkg_name, Some(&spec_str), cli_profile, &flags)?;
230→                        all_lines.extend(lines);
231→                    }
232→                }
233→                let counts = parse_test_results(&all_lines);
234→                if counts.total_ran() == 0 {
235→                    eprintln!("Error: 0 tests ran (filter may not match anything)");
236→                    return Ok(ExitCode::from(1));
237→                }
238→                Ok(ExitCode::SUCCESS)
239→            }
240→        }
241→    }
242→}
243→
244→/// Format a cargo test target header into a human-friendly label.
245→///
246→/// Cargo emits headers like:
247→/// - `unittests src/lib.rs (target/debug/deps/tspec-abc123)` → unit tests (no --test name)
248→/// - `tests/tspec_test.rs (target/debug/deps/tspec_test-abc123)` → integration test
249→///
250→/// For integration tests, we show the `--test` usable name prominently.
251→fn format_target_header(raw: &str) -> String {
252→    // Strip the "(target/...)" suffix for cleaner output
253→    let label = if let Some(idx) = raw.find(" (") {
254→        &raw[..idx]
255→    } else {
256→        raw
257→    };
258→
259→    if label.starts_with("unittests ") {
260→        // "unittests src/lib.rs" → unit tests, not selectable with --test
261→        format!("unit tests ({})", label.strip_prefix("unittests ").unwrap())
262→    } else if label.starts_with("tests/") {
263→        // "tests/tspec_test.rs" → extract basename for --test
264→        let basename = label
265→            .strip_prefix("tests/")
266→            .and_then(|s| s.strip_suffix(".rs"))
267→            .unwrap_or(label);
268→        format!("{} (--test {})", label, basename)
269→    } else {
270→        label.to_string()
271→    }
272→}
273→
274→/// List available target names for `--test`.
275→///
276→/// Runs `cargo test --no-run` and parses stderr for "Running tests/..." lines,
277→/// extracting the basename (without .rs) as the target name.
278→fn list_target_names(package: Option<&str>, flags: &CargoFlags) -> Result<ExitCode> {
279→    let workspace = find_project_root()?;
280→
281→    let mut cmd = std::process::Command::new("cargo");
282→    cmd.arg("test").arg("--no-run");
283→    if let Some(pkg) = package {
284→        cmd.arg("-p").arg(pkg);
285→    }
286→    flags.apply_to_command(&mut cmd);
287→    cmd.current_dir(&workspace);
288→
289→    let output = cmd.output().context("failed to run cargo test --no-run")?;
290→
291→    if !output.status.success() {
292→        let stderr = String::from_utf8_lossy(&output.stderr);
293→        eprintln!("{}", stderr);
294→        return Ok(ExitCode::from(1));
295→    }
296→
297→    let stderr = String::from_utf8_lossy(&output.stderr);
298→    let mut found = false;
299→
300→    println!("Available target names for --test:");
301→    for line in stderr.lines() {
302→        let trimmed = line.trim();
303→        // --no-run emits "Executable tests/foo.rs (target/...)" for integration tests
304→        if let Some(rest) = trimmed
305→            .strip_prefix("Executable tests/")
306→            .or_else(|| trimmed.strip_prefix("Running tests/"))
307→        {
308→            let file = if let Some(idx) = rest.find(" (") {
309→                &rest[..idx]
310→            } else {
311→                rest
312→            };
313→            if let Some(name) = file.strip_suffix(".rs") {
314→                println!("  {}    (tests/{})", name, file);
315→                found = true;
316→            }
317→        }
318→    }
319→
320→    if !found {
321→        println!("  (none — no integration test files in tests/)");
322→    }
323→
324→    Ok(ExitCode::SUCCESS)
325→}
326→
327→/// Run `cargo test -- --list` and format the output.
328→///
329→/// Groups test functions under their target headers, showing counts per target
330→/// and a total. Skips targets with zero tests.
331→fn list_tests(
332→    package: Option<&str>,
333→    name_filter: &[String],
334→    flags: &CargoFlags,
335→) -> Result<ExitCode> {
336→    let workspace = find_project_root()?;
337→
338→    let mut cmd = std::process::Command::new("cargo");
339→    cmd.arg("test");
340→    if let Some(pkg) = package {
341→        cmd.arg("-p").arg(pkg);
342→    }
343→    flags.apply_to_command(&mut cmd);
344→    cmd.arg("--");
345→    cmd.args(name_filter);
346→    cmd.arg("--list");
347→    cmd.current_dir(&workspace);
348→
349→    let output = cmd.output().context("failed to run cargo test -- --list")?;
350→
351→    if !output.status.success() {
352→        let stderr = String::from_utf8_lossy(&output.stderr);
353→        eprintln!("{}", stderr);
354→        return Ok(ExitCode::from(1));
355→    }
356→
357→    let stderr = String::from_utf8_lossy(&output.stderr);
358→    let stdout = String::from_utf8_lossy(&output.stdout);
359→
360→    // Parse target headers from stderr ("Running ..." and "Doc-tests ..." lines)
361→    // Produce human-friendly labels that show the --test name when applicable.
362→    let targets: Vec<String> = stderr
363→        .lines()
364→        .filter_map(|line| {
365→            let trimmed = line.trim();
366→            if trimmed.starts_with("Running ") {
367→                let rest = trimmed.strip_prefix("Running ").unwrap();
368→                Some(format_target_header(rest))
369→            } else if trimmed.starts_with("Doc-tests ") {
370→                Some(trimmed.to_string())
371→            } else {
372→                None
373→            }
374→        })
375→        .collect();
376→
377→    // Parse stdout into groups split by "N tests, M benchmarks" summary lines
378→    let mut groups: Vec<Vec<&str>> = Vec::new();
379→    let mut current: Vec<&str> = Vec::new();
380→    for line in stdout.lines() {
381→        if line.contains("tests,") && line.contains("benchmarks") {
382→            groups.push(std::mem::take(&mut current));
383→        } else if line.ends_with(": test") || line.ends_with(": bench") {
384→            current.push(line);
385→        }
386→    }
387→
388→    // Print grouped output
389→    let mut total = 0;
390→    for (i, tests) in groups.iter().enumerate() {
391→        if tests.is_empty() {
392→            continue;
393→        }
394→        let unknown = "unknown target".to_string();
395→        let header = targets.get(i).unwrap_or(&unknown);
396→        println!("{} ({} tests)", header, tests.len());
397→        for test in tests {
398→            // Strip ": test" / ": bench" suffix for cleaner output
399→            let name = test
400→                .strip_suffix(": test")
401→                .or_else(|| test.strip_suffix(": bench"))
402→                .unwrap_or(test);
403→            println!("  {}", name);
404→        }
405→        total += tests.len();
406→        println!();
407→    }
408→
409→    println!("{} tests total", total);
410→    Ok(ExitCode::SUCCESS)
411→}
