 16→const TSPEC_BUILD_RS_MARKER: &str = "// Generated by tspec - do not edit";
 17→
 18→/// Built-in cargo profiles that don't require a `[profile.<name>]` definition.
 19→const BUILTIN_PROFILES: &[&str] = &["dev", "debug", "release", "test", "bench"];
 20→
 21→/// Validate that a custom profile is defined in the workspace Cargo.toml.
 22→/// Built-in profiles (dev, release, test, bench) are always valid.
 23→/// Custom profiles must have a `[profile.<name>]` section with `inherits`.
 24→pub fn validate_profile(profile: &str, workspace: &Path) -> Result<()> {
 25→    if BUILTIN_PROFILES.contains(&profile) {
 26→        return Ok(());
 27→    }
 28→    let cargo_toml_path = workspace.join("Cargo.toml");
 29→    let content = fs::read_to_string(&cargo_toml_path)
 30→        .with_context(|| format!("failed to read {}", cargo_toml_path.display()))?;
 31→    let doc: toml::Value =
 32→        toml::from_str(&content).context("failed to parse workspace Cargo.toml")?;
 33→    if let Some(profiles) = doc.get("profile").and_then(|p| p.as_table())
 34→        && profiles.contains_key(profile)
 35→    {
 36→        return Ok(());
 37→    }
 38→    bail!(
 39→        "profile `{}` is not defined in {}. Custom profiles must be defined with \
 40→         `[profile.{}]` and an `inherits` field (e.g., `inherits = \"release\"`).",
 41→        profile,
 42→        cargo_toml_path.display(),
 43→        profile,
 44→    )
 45→}
 46→/// Check for spec settings that are likely misconfigurations.
 47→/// Returns a list of warning messages (printed at top and bottom of output).
 48→pub fn check_spec_misconfigurations(pkg_name: &str, spec: &Spec, pkg_dir: &Path) -> Vec<String> {
 49→    let mut warnings = Vec::new();
 50→    let has_linker_args = !spec.linker.args.is_empty();
 51→    let has_bin_target = pkg_dir.join("src/main.rs").exists();
 52→
 53→    // -static with non-musl target (glibc + -static segfaults)
 54→    if has_linker_args {
 55→        let has_static = spec.linker.args.iter().any(|a| a == "-static");
 56→        let is_musl = spec
 57→            .cargo
 58→            .target_triple
 59→            .as_ref()
 60→            .is_some_and(|t| t.contains("musl"));
 61→        if has_static && !is_musl {
 62→            warnings.push(format!(
 63→                "Warning: -static linker arg without musl target for {}. \
 64→                 glibc + -static often segfaults; consider using a musl target triple.",
 65→                pkg_name
 66→            ));
 67→        }
 68→    }
 69→
 70→    // linker.args on lib-only package
 71→    if has_linker_args && !has_bin_target {
 72→        warnings.push(format!(
 73→            "Warning: linker.args ignored for {} (no binary target)",
 74→            pkg_name
 75→        ));
 76→    }
 77→
 78→    warnings
 79→}
 80→
 81→/// Print collected warnings again so they're visible after cargo output.
 82→pub fn reprint_warnings(warnings: &[String]) {
 83→    for w in warnings {
 84→        println!("{}", w);
 85→    }
 86→}
