 94→/// Check if a build.rs file was generated by tspec.
 95→/// Matches either the marker comment or a file that only contains
 96→/// `cargo:rustc-link-arg-bin=` println lines (marker-less variant).
 97→fn is_tspec_generated_build_rs(content: &str) -> bool {
 98→    if content.starts_with(TSPEC_BUILD_RS_MARKER) {
 99→        return true;
100→    }

