10→/// Clean build artifacts
11→#[derive(Args)]
12→pub struct CleanCmd {
13→    /// Package to clean (name or path, e.g. "." for current dir)
14→    #[arg(value_name = "PACKAGE")]
15→    pub positional: Option<String>,
16→    /// Package to clean (defaults to entire workspace/project)
17→    #[arg(short = 'p', long = "package")]
18→    pub package: Option<String>,
19→    /// Only clean release artifacts
20→    #[arg(short, long)]
21→    pub release: bool,
22→}
23→
24→impl Execute for CleanCmd {
25→    fn execute(&self, project_root: &Path, flags: &CargoFlags) -> Result<ExitCode> {
26→        let mut args: Vec<OsString> = Vec::new();
27→        let pkg_arg = self.positional.as_ref().or(self.package.as_ref());
28→        if let Some(pkg) = pkg_arg
29→            && let Some(name) = resolve_package_arg(pkg)?
30→        {
31→            args.push("-p".into());
32→            args.push(name.into());
33→        }
34→        if self.release {
35→            args.push("--release".into());
36→        }
37→        execute_cargo_subcommand("clean", &args, project_root, flags)
38→    }
39→}
