 15→/// Build and run package(s) with a translation spec
 16→#[derive(Args)]
 17→pub struct RunCmd {
 18→    /// Package to run (name or path, e.g. "." for current dir)
 19→    #[arg(value_name = "PACKAGE")]
 20→    pub positional: Option<String>,
 21→    /// Package to run (defaults to current directory or all apps)
 22→    #[arg(short = 'p', long = "package")]
 23→    pub package: Option<String>,
 24→    /// Run all workspace apps (even when in a package directory)
 25→    #[arg(short = 'w', long = "workspace")]
 26→    pub workspace: bool,
 27→    /// Spec file(s) or glob pattern(s) to run with (defaults to package's tspec file)
 28→    #[arg(short = 't', long = "tspec", num_args = 1..)]
 29→    pub tspec: Vec<String>,
 30→    /// Release build
 31→    #[arg(short, long, conflicts_with = "profile")]
 32→    pub release: bool,
 33→    /// Build profile (e.g., release, release-small, or any custom profile)
 34→    #[arg(long)]
 35→    pub profile: Option<String>,
 36→    /// Strip symbols from binary before running
 37→    #[arg(short, long)]
 38→    pub strip: bool,
 39→    /// Arguments to pass to the binary (after --)
 40→    #[arg(last = true)]
 41→    pub args: Vec<String>,
 42→}
 43→
 44→impl RunCmd {
 45→    fn effective_profile(&self) -> Option<&str> {
 46→        if let Some(ref p) = self.profile {
 47→            Some(p)
 48→        } else if self.release {
 49→            Some("release")
 50→        } else {
 51→            None
 52→        }
 53→    }
 54→}
 55→
 56→impl Execute for RunCmd {
 57→    fn execute(&self, project_root: &Path, flags: &CargoFlags) -> Result<ExitCode> {
 58→        let cli_profile = self.effective_profile();
 59→
 60→        // Resolve package: --workspace > -p/positional PKG > cwd > all
 61→        let resolved = if self.workspace {
 62→            None
 63→        } else {
 64→            match self.positional.as_deref().or(self.package.as_deref()) {
 65→                Some(pkg) => resolve_package_arg(pkg)?,
 66→                None => current_package_name(),
 67→            }
 68→        };
 69→
 70→        match resolved {
 71→            None => {
 72→                // Run all apps (args not supported for --workspace)
 73→                let workspace = WorkspaceInfo::discover()?;
 74→                let results = run_all(&workspace, &self.tspec, cli_profile, self.strip, flags);
 75→                Ok(print_run_summary(workspace.name(), &results))
 76→            }
 77→            Some(name) => {
 78→                if self.tspec.is_empty() {
 79→                    // Build, optionally strip, then run
 80→                    let result = build_package(&name, None, cli_profile, flags)?;
 81→                    if self.strip {
 82→                        strip_binary(&result.binary_path)?;
 83→                    }
 84→                    let exit_code = run_binary(&result.binary_path, &self.args)?;
 85→                    std::process::exit(exit_code);
 86→                } else {
 87→                    let package_dir = resolve_package_dir(project_root, Some(&name))?;
 88→                    let pkg_name = get_package_name(&package_dir)?;
 89→                    let spec_paths = find_tspecs(&package_dir, &self.tspec)?;
 90→                    for spec_path in &spec_paths {
 91→                        let spec_str = spec_path.to_string_lossy();
 92→                        let result = build_package(&pkg_name, Some(&spec_str), cli_profile, flags)?;
 93→                        if self.strip {
 94→                            strip_binary(&result.binary_path)?;
 95→                        }
 96→                        let exit_code = run_binary(&result.binary_path, &self.args)?;
 97→                        if exit_code != 0 {
 98→                            std::process::exit(exit_code);
 99→                        }
100→                    }
101→                    Ok(ExitCode::SUCCESS)
102→                }
103→            }
104→        }
105→    }
106→}
