use clap::Parser;
use std::process::ExitCode;

use tspec::cli::{Cli, Commands};
use tspec::cmd::Execute;
use tspec::find_paths::find_project_root;
use tspec::types::{CargoFlags, Verbosity};

fn main() -> Result<ExitCode, anyhow::Error> {
    let cli = Cli::parse();
    let flags = CargoFlags {
        verbosity: Verbosity::from_count(cli.verbose),
        jobs: cli.jobs,
        extra_args: Vec::new(),
    };

    match cli.command {
        Commands::Build(cmd) => {
            return cmd.execute(&find_project_root()?, &flags);
        }
        Commands::Run(cmd) => {
            return cmd.execute(&find_project_root()?, &flags);
        }
        Commands::Test(cmd) => {
            return cmd.execute(&find_project_root()?, &flags);
        }
        Commands::Clean(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
        Commands::Clippy(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
        Commands::Fmt(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
        Commands::Compare(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
        Commands::Ts(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
        Commands::Version(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
        Commands::Install(cmd) => {
            cmd.execute(&find_project_root()?, &flags)?;
        }
    }

    Ok(ExitCode::SUCCESS)
}

use crate::cmd::{
    BuildCmd, CleanCmd, ClippyCmd, CompareCmd, FmtCmd, InstallCmd, RunCmd, TestCmd, TsCmd,
    VersionCmd,
};
use clap::{ArgAction, Parser, Subcommand};

#[derive(Parser)]
#[command(name = "tspec", version, about = "Translation spec based build system")]
#[command(before_help = concat!("tspec ", env!("CARGO_PKG_VERSION")))]
pub struct Cli {
    /// Increase verbosity (-v for commands/env + cargo -v, -vv for spec details + cargo -vv)
    #[arg(short = 'v', long = "verbose", action = ArgAction::Count, global = true)]
    pub verbose: u8,
    /// Number of parallel jobs to pass to cargo
    #[arg(short = 'j', long = "jobs", global = true)]
    pub jobs: Option<u16>,
    #[command(subcommand)]
    pub command: Commands,
}

#[derive(Subcommand)]
pub enum Commands {
    /// Build package(s) with a translation spec
    Build(BuildCmd),
    /// Build and run package(s) with a translation spec
    Run(RunCmd),
    /// Test package(s) with a translation spec
    Test(TestCmd),
    /// Clean build artifacts
    Clean(CleanCmd),
    /// Run clippy lints
    Clippy(ClippyCmd),
    /// Format source code
    Fmt(FmtCmd),
    /// Compare specs for a package (size only)
    Compare(CompareCmd),
    /// Manage translation specs
    Ts(TsCmd),
    /// Print version information
    Version(VersionCmd),
    /// Install a package from a local path
    Install(InstallCmd),
}

