  1→use anyhow::{Context, Result, bail};
  2→use glob::Pattern;
  3→use std::path::{Path, PathBuf};
  4→
  5→use crate::TSPEC_SUFFIX;
  6→use crate::types::{Spec, profile_dir_name};
  7→
  8→/// Check if TOML content has an exact section header like `[workspace]` or `[package]`
  9→/// Only matches lines where the trimmed content equals `[section]` exactly
 10→fn has_toml_section_exact(content: &str, section: &str) -> bool {
 11→    let header = format!("[{}]", section);
 12→    content.lines().any(|line| line.trim() == header)
 13→}
 14→
 15→/// Extract package name from Cargo.toml
 16→pub fn get_package_name(crate_dir: &Path) -> Result<String> {
 17→    let cargo_toml = crate_dir.join("Cargo.toml");
 18→    let content = std::fs::read_to_string(&cargo_toml)
 19→        .with_context(|| format!("failed to read {}", cargo_toml.display()))?;
 20→
 21→    // Simple parsing - look for name = "..." in [package] section
 22→    let mut in_package = false;
 23→    for line in content.lines() {
 24→        let line = line.trim();
 25→        if line == "[package]" {
 26→            in_package = true;
 27→            continue;
 28→        }
 29→        if line.starts_with('[') {
 30→            in_package = false;
 31→            continue;
 32→        }
 33→        if in_package
 34→            && line.starts_with("name")
 35→            && let Some(eq_pos) = line.find('=')
 36→        {
 37→            let value = line[eq_pos + 1..].trim();
 38→            let value = value.trim_matches('"').trim_matches('\'');
 39→            return Ok(value.to_string());
 40→        }
 41→    }
 42→    bail!("could not find package name in {}", cargo_toml.display());
 43→}
 44→
 45→/// Find the project root by looking for Cargo.toml with [workspace] or [package]
 46→/// For workspaces, returns the directory containing the workspace Cargo.toml
 47→/// For POPs (Plain Old Packages), returns the directory containing the package Cargo.toml
 48→/// Respects workspace `exclude` — a package inside an excluded path is treated as a POP.
 49→pub fn find_project_root() -> Result<PathBuf> {
 50→    let mut dir = std::env::current_dir()?;
 51→    let mut package_root: Option<PathBuf> = None;
 52→
 53→    loop {
 54→        let cargo_toml = dir.join("Cargo.toml");
 55→        if cargo_toml.exists() {
 56→            let content = std::fs::read_to_string(&cargo_toml)?;
 57→            // Workspace takes precedence — unless the package we found is excluded
 58→            if has_toml_section_exact(&content, "workspace") {
 59→                if let Some(ref pkg_root) = package_root
 60→                    && is_excluded_from_workspace(&content, &dir, pkg_root)
 61→                {
 62→                    return Ok(pkg_root.clone());
 63→                }
 64→                return Ok(dir);
 65→            }
 66→            // Remember the first (deepest) package we find as potential POP root
 67→            if package_root.is_none() && has_toml_section_exact(&content, "package") {
 68→                package_root = Some(dir.clone());
 69→            }
 70→        }
 71→        if !dir.pop() {
 72→            // No workspace found, use the POP root if we found one
 73→            if let Some(root) = package_root {
 74→                return Ok(root);
 75→            }
 76→            bail!("could not find project root (no Cargo.toml with [workspace] or [package])");
 77→        }
 78→    }
 79→}
