 81→/// Check if a package directory is excluded from a workspace.
 82→/// `ws_content` is the workspace Cargo.toml content, `ws_dir` is the workspace root,
 83→/// and `pkg_dir` is the package directory to check.
 84→fn is_excluded_from_workspace(ws_content: &str, ws_dir: &Path, pkg_dir: &Path) -> bool {
 85→    let Ok(rel) = pkg_dir.strip_prefix(ws_dir) else {
 86→        return false;
 87→    };
 88→    let rel_str = rel.to_string_lossy();
 89→
 90→    // Parse workspace.exclude from the TOML
 91→    let Ok(doc) = ws_content.parse::<toml::Value>() else {
 92→        return false;
 93→    };
 94→    let Some(excludes) = doc
 95→        .get("workspace")
 96→        .and_then(|w| w.get("exclude"))
 97→        .and_then(|e| e.as_array())
 98→    else {
 99→        return false;
100→    };
101→
102→    for exclude in excludes {
103→        let Some(pattern) = exclude.as_str() else {
104→            continue;
105→        };
106→        // Check if the package path starts with the exclude pattern
107→        // e.g., exclude = ["tests/fixtures"] should match "tests/fixtures/pop"
108→        if rel_str == pattern || rel_str.starts_with(&format!("{}/", pattern)) {
109→            return true;
110→        }
111→        // Also try glob matching
112→        if let Ok(glob) = Pattern::new(pattern)
113→            && glob.matches(&rel_str)
114→        {
115→            return true;
116→        }
117→    }
118→
119→    false
120→}
