134→/// Resolve package directory from optional name, defaulting to current directory
135→/// If package is None, uses current directory (must contain Cargo.toml)
136→/// If package is Some, looks up the package by name
137→pub fn resolve_package_dir(workspace: &Path, package: Option<&str>) -> Result<PathBuf> {
138→    match package {
139→        Some(name) => find_package_dir(workspace, name),
140→        None => {
141→            let cwd = std::env::current_dir()?;
142→            if cwd.join("Cargo.toml").exists() {
143→                Ok(cwd)
144→            } else {
145→                bail!(
146→                    "not in a package directory (no Cargo.toml found). Use -p to specify a package."
147→                )
148→            }
149→        }
150→    }
151→}
152→
153→/// Find a package's directory - tries as path first, then searches standard locations
154→/// For POPs, checks if name matches the root package
155→pub fn find_package_dir(project_root: &Path, name: &str) -> Result<PathBuf> {
156→    // Try as path first (relative or absolute)
157→    let as_path = PathBuf::from(name);
158→    if as_path.join("Cargo.toml").exists() {
159→        return Ok(as_path.canonicalize().unwrap_or(as_path));
160→    }
161→
162→    // Check if name matches the root package
163→    if let Ok(pkg_name) = get_package_name(project_root)
164→        && pkg_name == name
165→    {
166→        return Ok(project_root.to_path_buf());
167→    }
168→
169→    // For POPs, nothing else to search
170→    if is_pop(project_root) {
171→        bail!(
172→            "package '{}' not found (this is a single-package project with package '{}')",
173→            name,
174→            get_package_name(project_root).unwrap_or_else(|_| "unknown".to_string())
175→        );
176→    }
177→
178→    // Workspace: search root-level members, then libs/, apps/, tools/
179→    let root_path = project_root.join(name);
180→    if root_path.join("Cargo.toml").exists() {
181→        return Ok(root_path);
182→    }
183→
184→    for prefix in ["libs", "apps", "tools"] {
185→        let path = project_root.join(prefix).join(name);
186→        if path.join("Cargo.toml").exists() {
187→            return Ok(path);
188→        }
189→    }
190→
191→    // Special case: nested test crates like libs/rlibc-x2/tests
192→    for prefix in ["libs", "apps"] {
193→        for entry in std::fs::read_dir(project_root.join(prefix))
194→            .into_iter()
195→            .flatten()
196→            .flatten()
197→        {
198→            let nested = entry.path().join("tests");
199→            if nested.join("Cargo.toml").exists()
200→                && let Ok(pkg_name) = get_package_name(&nested)
201→                && pkg_name == name
202→            {
203→                return Ok(nested);
204→            }
205→        }
206→    }
207→
208→    bail!("package '{}' not found", name);
209→}
