211→/// Find the tspec for a package - tries as path first, then relative to pkg_dir
212→/// Returns None if no tspec exists (plain cargo build will be used)
213→pub fn find_tspec(pkg_dir: &Path, explicit: Option<&str>) -> Result<Option<PathBuf>> {
214→    match explicit {
215→        Some(name) => {
216→            // Try as path first (relative to cwd or absolute)
217→            let as_path = PathBuf::from(name);
218→            if as_path.exists() {
219→                return Ok(Some(as_path.canonicalize().unwrap_or(as_path)));
220→            }
221→
222→            // Fallback: relative to package directory
223→            let in_pkg = pkg_dir.join(name);
224→            if in_pkg.exists() {
225→                return Ok(Some(in_pkg));
226→            }
227→
228→            // Try with TSPEC_SUFFIX if name has no extension
229→            if !name.contains('.') {
230→                let with_suffix = pkg_dir.join(format!("{}{}", name, TSPEC_SUFFIX));
231→                if with_suffix.exists() {
232→                    return Ok(Some(with_suffix));
233→                }
234→            }
235→
236→            bail!("tspec not found: {}", name);
237→        }
238→        None => {
239→            let default = pkg_dir.join(format!("tspec{}", TSPEC_SUFFIX));
240→            if default.exists() {
241→                Ok(Some(default))
242→            } else {
243→                Ok(None) // No tspec = plain cargo build
244→            }
245→        }
246→    }
247→}
248→
249→/// Find multiple tspecs by glob patterns
250→/// If no patterns given, defaults to "tspec*{TSPEC_SUFFIX}"
251→/// Returns sorted list of paths, errors if none found
252→pub fn find_tspecs(pkg_dir: &Path, patterns: &[String]) -> Result<Vec<PathBuf>> {
253→    let default_pattern = format!("tspec*{}", TSPEC_SUFFIX);
254→    let patterns: Vec<&str> = if patterns.is_empty() {
255→        vec![&default_pattern]
256→    } else {
257→        patterns.iter().map(|s| s.as_str()).collect()
258→    };
259→
260→    let mut results = Vec::new();
261→
262→    for pattern in &patterns {
263→        // Try as literal path first (relative to cwd or absolute)
264→        let as_path = PathBuf::from(pattern);
265→        if as_path.exists() && as_path.is_file() {
266→            results.push(as_path.canonicalize().unwrap_or(as_path));
267→            continue;
268→        }
269→
270→        // Try as literal path relative to pkg_dir
271→        let in_pkg = pkg_dir.join(pattern);
272→        if in_pkg.exists() && in_pkg.is_file() {
273→            results.push(in_pkg);
274→            continue;
275→        }
276→
277→        // Try as glob pattern in pkg_dir
278→        let glob_pattern =
279→            Pattern::new(pattern).with_context(|| format!("invalid glob pattern: {}", pattern))?;
280→
281→        let entries: Vec<_> = std::fs::read_dir(pkg_dir)
282→            .with_context(|| format!("cannot read directory: {}", pkg_dir.display()))?
283→            .filter_map(|e| e.ok())
284→            .filter(|e| {
285→                let name = e.file_name().to_string_lossy().to_string();
286→                e.path().is_file() && glob_pattern.matches(&name)
287→            })
288→            .map(|e| e.path())
289→            .collect();
290→
291→        results.extend(entries);
292→    }
293→
294→    // Remove duplicates and sort
295→    results.sort();
296→    results.dedup();
297→
298→    if results.is_empty() {
299→        let pattern_list = patterns.join(", ");
300→        bail!(
301→            "no tspec files found matching '{}' in {}",
302→            pattern_list,
303→            pkg_dir.display()
304→        );
305→    }
306→
307→    Ok(results)
308→}
