use clap::Parser;
use std::process::ExitCode;

use tspec::cli::{Cli, Commands};
use tspec::cmd::Execute;
use tspec::find_paths::find_project_root;

fn main() -> Result<ExitCode, anyhow::Error> {
    let cli = Cli::parse();

    match cli.command {
        Commands::Build(cmd) => {
            return cmd.execute(&find_project_root()?);
        }
        Commands::Run(cmd) => {
            return cmd.execute(&find_project_root()?);
        }
        Commands::Test(cmd) => {
            return cmd.execute(&find_project_root()?);
        }
        Commands::Clean(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
        Commands::Clippy(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
        Commands::Fmt(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
        Commands::Compare(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
        Commands::Ts(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
        Commands::Version(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
        Commands::Install(cmd) => {
            cmd.execute(&find_project_root()?)?;
        }
    }

    Ok(ExitCode::SUCCESS)
}

use crate::cmd::{
    BuildCmd, CleanCmd, ClippyCmd, CompareCmd, FmtCmd, InstallCmd, RunCmd, TestCmd, TsCmd,
    VersionCmd,
};
use clap::{Parser, Subcommand};

#[derive(Parser)]
#[command(name = "tspec", version, about = "Translation spec based build system")]
#[command(before_help = concat!("tspec ", env!("CARGO_PKG_VERSION")))]
pub struct Cli {
    #[command(subcommand)]
    pub command: Commands,
}

#[derive(Subcommand)]
pub enum Commands {
    /// Build package(s) with a translation spec
    Build(BuildCmd),
    /// Build and run package(s) with a translation spec
    Run(RunCmd),
    /// Test package(s) with a translation spec
    Test(TestCmd),
    /// Clean build artifacts
    Clean(CleanCmd),
    /// Run clippy lints
    Clippy(ClippyCmd),
    /// Format source code
    Fmt(FmtCmd),
    /// Compare specs for a package (size only)
    Compare(CompareCmd),
    /// Manage translation specs
    Ts(TsCmd),
    /// Print version information
    Version(VersionCmd),
    /// Install a package from a local path
    Install(InstallCmd),
}

