     8→use crate::binary::{binary_size, strip_binary};
     9→use crate::cargo_build::build_crate;
    10→use crate::run::run_binary;
    11→use crate::testing::test_crate;
    12→use crate::workspace::{CrateKind, WorkspaceInfo};
    13→use crate::{print_header, print_hline};
    14→
    15→/// Result of a batch operation on a single crate
    16→pub struct OpResult {
    17→    pub name: String,
    18→    pub success: bool,
    19→    pub message: String,
    20→    pub size: Option<u64>,
    21→}
    22→
    23→/// Build all workspace crates (excluding build tools)
    24→pub fn build_all(
    25→    workspace: &WorkspaceInfo,
    26→    tspec: Option<&str>,
    27→    release: bool,
    28→    strip: bool,
    29→    fail_fast: bool,
    30→) -> Vec<OpResult> {
    31→    let mut results = Vec::new();
    32→
    33→    for member in workspace.buildable_members() {
    34→        println!("=== {} ===", member.name);
    35→
    36→        let result = match build_crate(&member.name, tspec, release) {
    37→            Ok(build_result) => {
    38→                if strip
    39→                    && member.has_binary
    40→                    && let Err(e) = strip_binary(&build_result.binary_path)
    41→                {
    42→                    eprintln!("  warning: strip failed: {}", e);
    43→                }
    44→                let size = binary_size(&build_result.binary_path).ok();
    45→                OpResult {
    46→                    name: member.name.clone(),
    47→                    success: true,
    48→                    message: format!("{}", build_result.binary_path.display()),
    49→                    size,
    50→                }
    51→            }
    52→            Err(e) => OpResult {
    53→                name: member.name.clone(),
    54→                success: false,
    55→                message: e.to_string(),
    56→                size: None,
    57→            },
    58→        };
    59→
    60→        let failed = !result.success;
    61→        results.push(result);
    62→
    63→        if failed && fail_fast {
    64→            break;
    65→        }
    66→    }
    67→
    68→    results
    69→}
    70→
    71→/// Run all app crates sequentially
    72→pub fn run_all(
    73→    workspace: &WorkspaceInfo,
    74→    tspec: Option<&str>,
    75→    release: bool,
    76→    strip: bool,
    77→) -> Vec<OpResult> {
    78→    let mut results = Vec::new();
    79→
    80→    for member in workspace.runnable_members() {
    81→        println!("=== {} ===", member.name);
    82→
    83→        let result = match build_crate(&member.name, tspec, release) {
    84→            Ok(build_result) => {
    85→                if strip && let Err(e) = strip_binary(&build_result.binary_path) {
    86→                    eprintln!("  warning: strip failed: {}", e);
    87→                }
    88→                match run_binary(&build_result.binary_path, &[]) {
    89→                    Ok(exit_code) => OpResult {
    90→                        name: member.name.clone(),
    91→                        success: true, // We don't treat non-zero exit as failure
    92→                        message: format!("exit code: {}", exit_code),
    93→                        size: None,
    94→                    },
    95→                    Err(e) => OpResult {
    96→                        name: member.name.clone(),
    97→                        success: false,
    98→                        message: format!("run failed: {}", e),
    99→                        size: None,
   100→                    },
   101→                }
   102→            }
   103→            Err(e) => OpResult {
   104→                name: member.name.clone(),
   105→                success: false,
   106→                message: format!("build failed: {}", e),
   107→                size: None,
   108→            },
   109→        };
   110→
   111→        results.push(result);
   112→    }
   113→
   114→    results
   115→}
   116→
   117→/// Test all workspace crates
   118→pub fn test_all(
   119→    workspace: &WorkspaceInfo,
   120→    tspec: Option<&str>,
   121→    release: bool,
   122→    fail_fast: bool,
   123→) -> Vec<OpResult> {
   124→    let mut results = Vec::new();
   125→
   126→    // Test regular crates (excluding Test kind which needs special handling)
   127→    for member in workspace.buildable_members() {
   128→        if member.kind == CrateKind::Test {
   129→            continue; // Handle test crates separately
   130→        }
   131→
   132→        println!("=== {} ===", member.name);
   133→
   134→        let result = match test_crate(&member.name, tspec, release) {
   135→            Ok(()) => OpResult {
   136→                name: member.name.clone(),
   137→                success: true,
   138→                message: "ok".to_string(),
   139→                size: None,
   140→            },
   141→            Err(e) => OpResult {
   142→                name: member.name.clone(),
   143→                success: false,
   144→                message: e.to_string(),
   145→                size: None,
   146→            },
   147→        };
   148→
   149→        let failed = !result.success;
   150→        results.push(result);
   151→
   152→        if failed && fail_fast {
   153→            return results;
   154→        }
   155→    }
   156→
   157→    // Handle test crates (like rlibc-x2-tests) - build and run all test binaries
   158→    for member in workspace.test_members() {
   159→        println!("=== {} ===", member.name);
   160→
   161→        // Build the test crate (builds all binaries)
   162→        let build_result = match build_crate(&member.name, tspec, release) {
   163→            Ok(r) => r,
   164→            Err(e) => {
   165→                results.push(OpResult {
   166→                    name: member.name.clone(),
   167→                    success: false,
   168→                    message: format!("build failed: {}", e),
   169→                    size: None,
   170→                });
   171→                if fail_fast {
   172→                    return results;
   173→                }
   174→                continue;
   175→            }
   176→        };
   177→
   178→        // Find and run all test binaries in the target directory
   179→        let profile = if release { "release" } else { "debug" };
   180→        let target_dir = build_result.target_base.join(profile);
   181→
   182→        // Look for binaries that end with "-tests" in the target directory
   183→        let test_binaries: Vec<_> = std::fs::read_dir(&target_dir)
   184→            .into_iter()
   185→            .flatten()
   186→            .filter_map(|e| e.ok())
   187→            .filter(|e| {
   188→                let name = e.file_name().to_string_lossy().to_string();
   189→                let is_file = e.path().is_file();
   190→                let is_test = name.ends_with("-tests");
   191→                let is_executable = e
   192→                    .metadata()
   193→                    .map(|m| m.permissions().mode() & 0o111 != 0)
   194→                    .unwrap_or(false);
   195→                is_file && is_test && is_executable
   196→            })
   197→            .collect();
   198→
   199→        if test_binaries.is_empty() {
   200→            results.push(OpResult {
   201→                name: member.name.clone(),
   202→                success: false,
   203→                message: "no test binaries found".to_string(),
   204→                size: None,
   205→            });
   206→            continue;
   207→        }
   208→
   209→        for entry in test_binaries {
   210→            let path = entry.path();
   211→            let bin_name = entry.file_name().to_string_lossy().to_string();
   212→
   213→            print!("  Running {}... ", bin_name);
   214→
   215→            let result = match run_binary(&path, &[]) {
   216→                Ok(exit_code) => {
   217→                    if exit_code == 0 {
   218→                        println!("ok");
   219→                        OpResult {
   220→                            name: format!("{}/{}", member.name, bin_name),
   221→                            success: true,
   222→                            message: "ok".to_string(),
   223→                            size: None,
   224→                        }
   225→                    } else {
   226→                        println!("FAILED (exit {})", exit_code);
   227→                        OpResult {
   228→                            name: format!("{}/{}", member.name, bin_name),
   229→                            success: false,
   230→                            message: format!("exit code: {}", exit_code),
   231→                            size: None,
   232→                        }
   233→                    }
   234→                }
   235→                Err(e) => {
   236→                    println!("FAILED");
   237→                    OpResult {
   238→                        name: format!("{}/{}", member.name, bin_name),
   239→                        success: false,
   240→                        message: format!("run failed: {}", e),
   241→                        size: None,
   242→                    }
   243→                }
   244→            };
   245→
   246→            let failed = !result.success;
   247→            results.push(result);
   248→
   249→            if failed && fail_fast {
   250→                return results;
   251→            }
   252→        }
   253→    }
   254→
   255→    results
   256→}
   257→
   258→/// Print a summary of operation results (for tests)
   259→pub fn print_test_summary(results: &[OpResult]) -> ExitCode {
   260→    let max_name_len = results
   261→        .iter()
   262→        .map(|r| r.name.len())
   263→        .max()
   264→        .unwrap_or(5)
   265→        .max(5);
   266→
   267→    println!();
   268→    print_header!("TEST SUMMARY");
   269→    println!("  {:width$}  Status", "Crate", width = max_name_len);
   270→
   271→    let mut passed = 0;
   272→    let mut failed = 0;
   273→
   274→    for result in results {
   275→        let status = if result.success {
   276→            passed += 1;
   277→            "[PASS]"
   278→        } else {
   279→            failed += 1;
   280→            "[FAIL]"
   281→        };
   282→        println!("  {:width$}  {status}", result.name, width = max_name_len);
   283→    }
   284→
   285→    println!();
   286→    println!("  Test: {} passed, {} failed", passed, failed);
   287→    println!();
   288→    println!("  Note: Run `tspec test -p tspec` or `cargo test -p tspec` to test tspec itself");
   289→    print_hline!();
   290→    println!();
   291→
   292→    if failed > 0 {
   293→        ExitCode::from(1)
