     1→use anyhow::{Context, Result, bail};
     2→use std::fs;
     3→use std::io::Write;
     4→use std::path::{Path, PathBuf};
     5→use std::process::Command;
     6→
     7→use crate::find_paths::{
     8→    find_package_dir, find_project_root, find_tspec, get_binary_path, get_binary_path_simple,
     9→    get_crate_name,
    10→};
    11→use crate::tspec::{expand_target_dir, load_spec, spec_name_from_path};
    12→use crate::types::{OptLevel, PanicStrategy, Profile, Spec};
    13→
    14→/// Result of a successful build
    15→pub struct BuildResult {
    16→    pub binary_path: PathBuf,
    17→    /// The effective target directory base (e.g., "target/foo" if target_dir was set)
    18→    pub target_base: PathBuf,
    19→}
    20→
    21→/// Build a crate with a spec, returns the binary path on success
    22→pub fn build_crate(crate_name: &str, tspec: Option<&str>, release: bool) -> Result<BuildResult> {
    23→    let workspace = find_project_root()?;
    24→    let crate_dir = find_package_dir(&workspace, crate_name)?;
    25→    let tspec_path = find_tspec(&crate_dir, tspec)?;
    26→
    27→    // Get actual package name from Cargo.toml (needed when crate_name is a path)
    28→    let pkg_name = get_crate_name(&crate_dir)?;
    29→
    30→    // Track if we generated a build.rs
    31→    let build_rs_path = crate_dir.join("build.rs");
    32→    let had_build_rs = build_rs_path.exists();
    33→
    34→    // Load spec once (if present) and compute target_dir
    35→    let (spec, expanded_td) = if let Some(path) = &tspec_path {
    36→        let s = load_spec(path)?;
    37→        let name = spec_name_from_path(path);
    38→        let td = expand_target_dir(&s, &name)?;
    39→        (Some(s), td)
    40→    } else {
    41→        (None, None)
    42→    };
    43→
    44→    // Compute paths
    45→    let target_base = match &expanded_td {
    46→        Some(td) => workspace.join("target").join(td),
    47→        None => workspace.join("target"),
    48→    };
    49→    let binary_path = if let Some(spec) = &spec {
    50→        get_binary_path(&workspace, &pkg_name, spec, release, expanded_td.as_deref())
    51→    } else {
    52→        get_binary_path_simple(&workspace, &pkg_name, release)
    53→    };
    54→
    55→    // Apply spec if present, otherwise plain cargo build
    56→    let status = if let Some(spec) = &spec {
    57→        let path = tspec_path.as_ref().unwrap();
    58→        println!("Building {} with spec {}", pkg_name, path.display());
    59→
    60→        // Generate temporary build.rs for linker flags if needed
    61→        let has_linker_args = !spec.linker.args.is_empty();
    62→        if has_linker_args && !had_build_rs {
    63→            generate_build_rs(&build_rs_path, &pkg_name, spec)?;
    64→        }
    65→
    66→        let mut cmd = build_cargo_command(spec)?;
    67→        cmd.arg("build");
    68→        cmd.arg("-p").arg(&pkg_name);
    69→        cmd.current_dir(&workspace);
    70→
    71→        apply_spec_to_command(&mut cmd, spec, &workspace, release, expanded_td.as_deref())?;
    72→        cmd.status().context("failed to run cargo")?
    73→    } else {
    74→        println!("Building {} (no tspec)", pkg_name);
    75→        let mut cmd = Command::new("cargo");
    76→        cmd.arg("build");
    77→        cmd.arg("-p").arg(&pkg_name);
    78→        cmd.current_dir(&workspace);
    79→        if release {
    80→            cmd.arg("--release");
    81→        }
    82→        cmd.status().context("failed to run cargo")?
    83→    };
    84→
    85→    // Clean up generated build.rs (only if we created it)
    86→    if !had_build_rs && build_rs_path.exists() {
    87→        let _ = fs::remove_file(&build_rs_path);
    88→    }
    89→
    90→    if !status.success() {
    91→        bail!("cargo build failed");
    92→    }
    93→
    94→    println!("  {}", binary_path.display());
    95→    Ok(BuildResult {
    96→        binary_path,
    97→        target_base,
    98→    })
    99→}
   100→
   101→/// Generate a temporary build.rs with scoped linker flags from tspec.toml
   102→pub fn generate_build_rs(path: &Path, crate_name: &str, spec: &Spec) -> Result<()> {
   103→    let mut lines = vec![
   104→        "// Generated by tspec - do not edit".to_string(),
   105→        "fn main() {".to_string(),
   106→    ];
   107→
   108→    for arg in &spec.linker.args {
   109→        lines.push(format!(
   110→            "    println!(\"cargo:rustc-link-arg-bin={}={}\");",
   111→            crate_name, arg
   112→        ));
   113→    }
   114→
   115→    lines.push("}".to_string());
   116→    fs::write(path, lines.join("\n")).context("failed to write build.rs")?;
   117→    Ok(())
   118→}
   119→
   120→/// Check if spec requires nightly toolchain
   121→fn requires_nightly(spec: &Spec) -> bool {
   122→    // High-level panic mode may require nightly
   123→    let panic_needs_nightly = spec.panic.map(|p| p.requires_nightly()).unwrap_or(false);
   124→
   125→    // BuildStd requires nightly
   126→    let has_build_std = !spec.rustc.build_std.is_empty();
   127→
   128→    // Unstable cargo flags require nightly
   129→    let has_unstable = !spec.cargo.unstable.is_empty();
   130→
   131→    panic_needs_nightly || has_build_std || has_unstable
   132→}
   133→
   134→/// Build the base cargo command (with toolchain if needed)
   135→fn build_cargo_command(spec: &Spec) -> Result<Command> {
   136→    let mut cmd = Command::new("cargo");
   137→
   138→    if requires_nightly(spec) {
   139→        cmd.arg("+nightly");
   140→    }
   141→
   142→    Ok(cmd)
   143→}
   144→
   145→/// Apply spec parameters to a cargo command
   146→pub fn apply_spec_to_command(
   147→    cmd: &mut Command,
   148→    spec: &Spec,
   149→    workspace: &Path,
   150→    release: bool,
   151→    expanded_target_dir: Option<&str>,
   152→) -> Result<()> {
   153→    // Set custom target directory if specified
   154→    if let Some(td) = expanded_target_dir {
   155→        cmd.arg("--target-dir")
   156→            .arg(workspace.join("target").join(td));
   157→    }
   158→
   159→    // Handle high-level panic mode (cargo -Z flag)
   160→    if let Some(panic_mode) = spec.panic
   161→        && let Some(z_flag) = panic_mode.cargo_z_flag()
   162→    {
   163→        cmd.arg("-Z").arg(z_flag);
   164→    }
   165→
   166→    // Handle cargo config
   167→    let has_profile = spec.cargo.profile.is_some();
   168→    if let Some(ref profile) = spec.cargo.profile {
   169→        match profile {
   170→            Profile::Release => {
   171→                cmd.arg("--release");
   172→            }
   173→            Profile::Debug => {
   174→                // Debug is default, no flag needed
   175→            }
   176→        }
   177→    }
   178→
   179→    if let Some(ref triple) = spec.cargo.target_triple {
   180→        cmd.arg("--target").arg(triple);
   181→    }
   182→
   183→    if let Some(ref path) = spec.cargo.target_json {
   184→        cmd.arg("--target").arg(path);
   185→    }
   186→
   187→    for flag in &spec.cargo.unstable {
   188→        cmd.arg("-Z").arg(flag);
   189→    }
   190→
   191→    // If no profile in spec but release flag passed, use release
   192→    if !has_profile && release {
   193→        cmd.arg("--release");
   194→    }
   195→
   196→    // Collect rustc flags
   197→    let mut rustc_flags: Vec<String> = Vec::new();
   198→
   199→    // Handle high-level panic mode (rustc -C flag)
   200→    if let Some(panic_mode) = spec.panic
   201→        && let Some(panic_value) = panic_mode.rustc_panic_value()
   202→    {
   203→        rustc_flags.push(format!("-C panic={}", panic_value));
   204→    }
   205→
   206→    // Handle rustc config
   207→    let rustc = &spec.rustc;
   208→
   209→    if let Some(level) = &rustc.opt_level {
   210→        let lvl = match level {
   211→            OptLevel::O0 => "0",
   212→            OptLevel::O1 => "1",
   213→            OptLevel::O2 => "2",
   214→            OptLevel::O3 => "3",
   215→            OptLevel::Os => "s",
   216→            OptLevel::Oz => "z",
   217→        };
   218→        rustc_flags.push(format!("-C opt-level={}", lvl));
   219→    }
   220→
   221→    if let Some(strategy) = &rustc.panic {
   222→        let s = match strategy {
   223→            PanicStrategy::Abort => "abort",
   224→            PanicStrategy::Unwind => "unwind",
