     1→//! Workspace discovery and crate classification
     2→//!
     3→//! Uses `cargo metadata` to discover workspace members and classify them
     4→//! by type (app, lib, tool, test, build tool).
     5→
     6→use anyhow::{Context, Result};
     7→use cargo_metadata::MetadataCommand;
     8→use std::path::{Path, PathBuf};
     9→
    10→/// Crate classification for behavior differences
    11→#[derive(Debug, Clone, PartialEq, Eq)]
    12→pub enum CrateKind {
    13→    /// apps/* - application binaries, runnable
    14→    App,
    15→    /// libs/* - library crates
    16→    Lib,
    17→    /// tools/* - utility binaries
    18→    Tool,
    19→    /// */tests - test binary crates (special handling)
    20→    Test,
    21→    /// Build tools such as xtask or tspec, excluded by default
    22→    BuildTool,
    23→}
    24→
    25→/// Information about a workspace member
    26→#[derive(Debug, Clone)]
    27→pub struct CrateMember {
    28→    pub name: String,
    29→    pub path: PathBuf,
    30→    pub has_binary: bool,
    31→    pub kind: CrateKind,
    32→}
    33→
    34→/// Workspace information from cargo metadata
    35→pub struct WorkspaceInfo {
    36→    pub root: PathBuf,
    37→    pub members: Vec<CrateMember>,
    38→}
    39→
    40→impl WorkspaceInfo {
    41→    /// Discover workspace using cargo metadata
    42→    pub fn discover() -> Result<Self> {
    43→        let metadata = MetadataCommand::new()
    44→            .no_deps()
    45→            .exec()
    46→            .context("failed to run cargo metadata")?;
    47→
    48→        let root = metadata.workspace_root.as_std_path().to_path_buf();
    49→
    50→        let packages = metadata.workspace_packages();
    51→        let is_pop = packages.len() == 1;
    52→
    53→        let members: Vec<CrateMember> = packages
    54→            .iter()
    55→            .map(|pkg| {
    56→                let path = pkg
    57→                    .manifest_path
    58→                    .parent()
    59→                    .unwrap()
    60→                    .as_std_path()
    61→                    .to_path_buf();
    62→                let has_binary = pkg.targets.iter().any(|t| t.is_bin());
    63→                // POPs are always App — classify_crate is for multi-crate workspaces
    64→                let kind = if is_pop {
    65→                    CrateKind::App
    66→                } else {
    67→                    classify_crate(&path, &pkg.name)
    68→                };
    69→
    70→                CrateMember {
    71→                    name: pkg.name.clone(),
    72→                    path,
    73→                    has_binary,
    74→                    kind,
    75→                }
    76→            })
    77→            .collect();
    78→
    79→        Ok(WorkspaceInfo { root, members })
    80→    }
    81→
    82→    /// Get members excluding build tools such as xtask or tspec
    83→    pub fn buildable_members(&self) -> Vec<&CrateMember> {
    84→        self.members
    85→            .iter()
    86→            .filter(|m| m.kind != CrateKind::BuildTool)
    87→            .collect()
    88→    }
    89→
    90→    /// Get members that can be run (apps only - have binaries and are runnable)
    91→    pub fn runnable_members(&self) -> Vec<&CrateMember> {
    92→        self.members
    93→            .iter()
    94→            .filter(|m| m.has_binary && m.kind == CrateKind::App)
    95→            .collect()
    96→    }
    97→
    98→    /// Get test crates (special handling for rlibc-x2-tests etc.)
    99→    pub fn test_members(&self) -> Vec<&CrateMember> {
   100→        self.members
   101→            .iter()
   102→            .filter(|m| m.kind == CrateKind::Test)
   103→            .collect()
   104→    }
   105→}
   106→
   107→/// Classify a crate based on its path and name
   108→fn classify_crate(path: &Path, name: &str) -> CrateKind {
   109→    let path_str = path.to_string_lossy();
   110→
   111→    // Build tools at workspace root level
   112→    if name == "tspec" || name == "xt" || name == "xtask" {
   113→        return CrateKind::BuildTool;
   114→    }
   115→
   116→    // Test crates
   117→    if path_str.contains("/tests") || name.ends_with("-tests") {
   118→        return CrateKind::Test;
   119→    }
   120→
   121→    // Categorize by directory
   122→    if path_str.contains("apps/") {
   123→        CrateKind::App
   124→    } else if path_str.contains("libs/") {
   125→        CrateKind::Lib
   126→    } else if path_str.contains("tools/") {
   127→        CrateKind::Tool
   128→    } else {
   129→        // Unknown location, treat as build tool (excluded)
   130→        CrateKind::BuildTool
   131→    }
   132→}
   133→
   134→#[cfg(test)]
   135→mod tests {
   136→    use super::*;
   137→
   138→    #[test]
   139→    fn classify_app() {
   140→        let path = PathBuf::from("/workspace/apps/ex-x1");
   141→        assert_eq!(classify_crate(&path, "ex-x1"), CrateKind::App);
   142→    }
   143→
   144→    #[test]
   145→    fn classify_lib() {
   146→        let path = PathBuf::from("/workspace/libs/rlibc-x1");
   147→        assert_eq!(classify_crate(&path, "rlibc-x1"), CrateKind::Lib);
   148→    }
   149→
   150→    #[test]
   151→    fn classify_tool() {
   152→        let path = PathBuf::from("/workspace/tools/is-libc-used");
   153→        assert_eq!(classify_crate(&path, "is-libc-used"), CrateKind::Tool);
   154→    }
   155→
   156→    #[test]
   157→    fn classify_test_by_path() {
   158→        let path = PathBuf::from("/workspace/libs/rlibc-x2/tests");
   159→        assert_eq!(classify_crate(&path, "rlibc-x2-tests"), CrateKind::Test);
   160→    }
   161→
   162→    #[test]
   163→    fn classify_test_by_name() {
   164→        let path = PathBuf::from("/workspace/somewhere");
   165→        assert_eq!(classify_crate(&path, "foo-tests"), CrateKind::Test);
   166→    }
   167→
   168→    #[test]
   169→    fn classify_build_tool_xt() {
   170→        let path = PathBuf::from("/workspace/xt");
   171→        assert_eq!(classify_crate(&path, "xt"), CrateKind::BuildTool);
   172→    }
   173→
   174→    #[test]
   175→    fn classify_build_tool_xtask() {
   176→        let path = PathBuf::from("/workspace/xtask");
   177→        assert_eq!(classify_crate(&path, "xtask"), CrateKind::BuildTool);
   178→    }
   179→
   180→    #[test]
   181→    fn discover_works() {
   182→        // This test works for both workspaces and POPs
   183→        let info = WorkspaceInfo::discover();
   184→        if let Ok(info) = info {
   185→            // Should have some members (at least 1 for POP, more for workspace)
   186→            assert!(!info.members.is_empty());
   187→
   188→            let buildable = info.buildable_members();
   189→            if info.members.len() == 1 {
   190→                // POP: the single crate should be buildable
   191→                assert_eq!(buildable.len(), 1);
   192→            } else {
   193→                // Workspace: xt and xtask should be excluded from buildable
   194→                assert!(
   195→                    buildable
   196→                        .iter()
