    24→        #[arg(short = 'w', long = "workspace")]
    25→        all: bool,
    26→    },
    27→    /// Show a tspec's contents
    28→    Show {
    29→        /// Package name (defaults to current directory)
    30→        #[arg(short = 'p', long = "package")]
    31→        package: Option<String>,
    32→        /// Show all workspace packages (even when in a package directory)
    33→        #[arg(short = 'w', long = "workspace")]
    34→        all: bool,
    35→        /// Tspec name (defaults to all tspec files)
    36→        #[arg(short = 't', long = "tspec")]
    37→        tspec: Option<String>,
    38→    },
    39→    /// Show the content hash of a tspec
    40→    Hash {
    41→        /// Package name (defaults to current directory)
    42→        #[arg(short = 'p', long = "package")]
    43→        package: Option<String>,
    44→        /// Hash all workspace packages (even when in a package directory)
    45→        #[arg(short = 'w', long = "workspace")]
    46→        all: bool,
    47→        /// Tspec name (defaults to package's tspec file)
    48→        #[arg(short = 't', long = "tspec")]
    49→        tspec: Option<String>,
    50→    },
    51→    /// Create a new tspec file
    52→    New {
    53→        /// Name for the new tspec (default: "tspec")
    54→        #[arg(default_value = "tspec")]
    55→        name: String,
    56→        /// Package name (defaults to current directory)
    57→        #[arg(short = 'p', long = "package")]
    58→        package: Option<String>,
    59→        /// Copy from existing tspec (package/spec or just spec name in same package)
    60→        #[arg(short = 'f', long = "from")]
    61→        from: Option<String>,
    62→    },
    63→    /// Set a scalar value in a tspec (modifies in place)
    64→    Set {
    65→        /// Key=value pair (e.g., "strip=symbols", "panic=abort", "rustc.lto=true")
    66→        assignment: String,
    67→        /// Package name (defaults to current directory)
    68→        #[arg(short = 'p', long = "package")]
    69→        package: Option<String>,
    70→        /// Tspec to modify (defaults to package's tspec.ts.toml)
    71→        #[arg(short = 't', long = "tspec")]
    72→        tspec: Option<String>,
    73→    },
    74→    /// Create a versioned backup of a tspec
    75→    Backup {
    76→        /// Package name (defaults to current directory)
    77→        #[arg(short = 'p', long = "package")]
    78→        package: Option<String>,
    79→        /// Tspec to backup (defaults to package's tspec.ts.toml)
    80→        #[arg(short = 't', long = "tspec")]
    81→        tspec: Option<String>,
    82→    },
    83→    /// Restore a tspec from a versioned backup
    84→    Restore {
    85→        /// Package name (defaults to current directory)
    86→        #[arg(short = 'p', long = "package")]
    87→        package: Option<String>,
    88→        /// Backup tspec to restore from (required, e.g., "t2-001-abcd1234.ts.toml")
    89→        #[arg(short = 't', long = "tspec")]
    90→        tspec: String,
    91→    },
    92→}
    93→
    94→impl Execute for TsCmd {
    95→    fn execute(&self, project_root: &Path) -> Result<ExitCode> {
    96→        match &self.command {
    97→            TsCommands::List { package, all } => {
    98→                ts_cmd::list_tspecs(project_root, package.as_deref(), *all)?;
    99→            }
   100→            TsCommands::Show {
   101→                package,
   102→                all,
   103→                tspec,
   104→            } => {
   105→                ts_cmd::show_tspec(project_root, package.as_deref(), *all, tspec.as_deref())?;
   106→            }
   107→            TsCommands::Hash {
   108→                package,
   109→                all,
   110→                tspec,
   111→            } => {
   112→                ts_cmd::hash_tspec(project_root, package.as_deref(), *all, tspec.as_deref())?;
   113→            }
   114→            TsCommands::New {
   115→                name,
   116→                package,
   117→                from,
   118→            } => {
   119→                ts_cmd::new_tspec(project_root, package.as_deref(), name, from.as_deref())?;
   120→            }
   121→            TsCommands::Set {
   122→                assignment,
   123→                package,
   124→                tspec,
   125→            } => {
   126→                let (key, value) = assignment.split_once('=').ok_or_else(|| {
   127→                    anyhow::anyhow!("invalid assignment '{}': expected key=value", assignment)
   128→                })?;
   129→                ts_cmd::set_value(
   130→                    project_root,
   131→                    package.as_deref(),
   132→                    key,
   133→                    value,
   134→                    tspec.as_deref(),
   135→                )?;
   136→            }
   137→            TsCommands::Backup { package, tspec } => {
   138→                ts_cmd::backup_tspec(project_root, package.as_deref(), tspec.as_deref())?;
   139→            }
   140→            TsCommands::Restore { package, tspec } => {
   141→                ts_cmd::restore_tspec(project_root, package.as_deref(), tspec)?;
   142→            }
   143→        }
   144→        Ok(ExitCode::SUCCESS)
   145→    }
   146→}
