  1→use anyhow::{Context, Result, bail};
  2→use std::fs;
  3→use std::io::Write;
  4→use std::path::{Path, PathBuf};
  5→use std::process::Command;
  6→
  7→use crate::find_paths::{
  8→    find_package_dir, find_project_root, find_tspec, get_binary_path, get_binary_path_simple,
  9→    get_package_name,
 10→};
 11→use crate::tspec::{expand_target_dir, load_spec, spec_name_from_path};
 12→use crate::types::{OptLevel, Profile, Spec};
 13→
 14→const TSPEC_BUILD_RS_MARKER: &str = "// Generated by tspec - do not edit";
 15→pub fn warn_stale_build_rs(had_stale: bool) {
 16→    if had_stale {
 17→        println!("Warning: a stale build.rs was deleted, likely from a previous interrupted build");
 18→    }
 19→}
 20→
 21→/// Check if a build.rs file was generated by tspec.
 22→/// Matches either the marker comment or a file that only contains
 23→/// `cargo:rustc-link-arg-bin=` println lines (marker-less variant).
 24→fn is_tspec_generated_build_rs(content: &str) -> bool {
 25→    if content.starts_with(TSPEC_BUILD_RS_MARKER) {
 26→        return true;
 27→    }
 28→    // Fallback: file only contains fn main, cargo:rustc-link-arg-bin printlns, and braces
 29→    let dominated_by_link_args = content
 30→        .lines()
 31→        .map(|l| l.trim())
 32→        .filter(|l| !l.is_empty())
 33→        .all(|l| {
 34→            l == "fn main() {"
 35→                || l == "}"
 36→                || l.starts_with(TSPEC_BUILD_RS_MARKER)
 37→                || l.contains("cargo:rustc-link-arg-bin=")
 38→        });
 39→    dominated_by_link_args && content.contains("cargo:rustc-link-arg-bin=")
 40→}
 41→
 42→/// Remove a stale tspec-generated build.rs left behind by an interrupted build.
 43→/// Returns true if a stale file was removed. Caller should print a warning after the build.
 44→pub fn remove_stale_tspec_build_rs(pkg_dir: &Path) -> bool {
 45→    let build_rs = pkg_dir.join("build.rs");
 46→    if let Ok(content) = fs::read_to_string(&build_rs)
 47→        && is_tspec_generated_build_rs(&content)
 48→    {
 49→        let _ = fs::remove_file(&build_rs);
 50→        return true;
 51→    }
 52→    false
 53→}
