189→/// Generate a temporary build.rs with scoped linker flags from tspec.toml
190→pub fn generate_build_rs(path: &Path, crate_name: &str, spec: &Spec) -> Result<()> {
191→    let mut lines = vec![TSPEC_BUILD_RS_MARKER.to_string(), "fn main() {".to_string()];
192→
193→    for arg in &spec.linker.args {
194→        lines.push(format!(
195→            "    println!(\"cargo:rustc-link-arg-bin={}={}\");",
196→            crate_name, arg
197→        ));
198→    }
199→
200→    lines.push("}".to_string());
201→    fs::write(path, lines.join("\n")).context("failed to write build.rs")?;
202→    Ok(())
203→}
