205→/// Check if spec requires nightly toolchain
206→fn requires_nightly(spec: &Spec) -> bool {
207→    // High-level panic mode may require nightly
208→    let panic_needs_nightly = spec.panic.map(|p| p.requires_nightly()).unwrap_or(false);
209→
210→    // BuildStd requires nightly
211→    let has_build_std = !spec.rustc.build_std.is_empty();
212→
213→    // Unstable cargo flags require nightly
214→    let has_unstable = !spec.cargo.unstable.is_empty();
215→
216→    panic_needs_nightly || has_build_std || has_unstable
217→}
218→
219→/// Build the base cargo command (with toolchain if needed)
220→fn build_cargo_command(spec: &Spec) -> Result<Command> {
221→    let mut cmd = Command::new("cargo");
222→
223→    if requires_nightly(spec) {
224→        cmd.arg("+nightly");
225→    }
226→
227→    Ok(cmd)
228→}
