 14const TSPEC_BUILD_RS_MARKER: &str = "// Generated by tspec - do not edit";
 15pub fn warn_stale_build_rs(had_stale: bool) {
 16    if had_stale {
 17        println!("Warning: a stale build.rs was deleted, likely from a previous interrupted build");
 18    }
