 42/// Remove a stale tspec-generated build.rs left behind by an interrupted build.
 43/// Returns true if a stale file was removed. Caller should print a warning after the build.
 44pub fn remove_stale_tspec_build_rs(pkg_dir: &Path) -> bool {
 45    let build_rs = pkg_dir.join("build.rs");
 46    if let Ok(content) = fs::read_to_string(&build_rs)
 47        && is_tspec_generated_build_rs(&content)
 48    {
 49        let _ = fs::remove_file(&build_rs);
 50        return true;
 51    }
 52    false
 53}
