156/// Plain `cargo build --release` with no spec lookup.
157/// Used by compare to produce a baseline build.
158pub fn plain_cargo_build_release(pkg_name: &str) -> Result<BuildResult> {
159    let workspace = find_project_root()?;
160    let pkg_dir = find_package_dir(&workspace, pkg_name)?;
161    let pkg_name = get_package_name(&pkg_dir)?;
162
163    // Clean up any stale tspec-generated build.rs from interrupted builds
164    let had_stale_build_rs = remove_stale_tspec_build_rs(&pkg_dir);
165
166    let binary_path = get_binary_path_simple(&workspace, &pkg_name, true);
167    let target_base = workspace.join("target");
168
169    println!("Building {} (no tspec)", pkg_name);
170    let mut cmd = Command::new("cargo");
171    cmd.arg("build");
172    cmd.arg("-p").arg(&pkg_name);
173    cmd.current_dir(&workspace);
174    cmd.arg("--release");
175    let status = cmd.status().context("failed to run cargo")?;
176
177    if !status.success() {
178        bail!("cargo build failed");
179    }
180
181    println!("  {}", binary_path.display());
182    warn_stale_build_rs(had_stale_build_rs);
183    Ok(BuildResult {
184        binary_path,
185        target_base,
186    })
187}
