230/// Apply spec parameters to a cargo command
231pub fn apply_spec_to_command(
232    cmd: &mut Command,
233    spec: &Spec,
234    workspace: &Path,
235    release: bool,
236    expanded_target_dir: Option<&str>,
237) -> Result<()> {
238    // Set custom target directory if specified
239    if let Some(td) = expanded_target_dir {
240        cmd.arg("--target-dir")
241            .arg(workspace.join("target").join(td));
242    }
243
244    // Handle high-level panic mode (cargo -Z flag)
245    if let Some(panic_mode) = spec.panic
246        && let Some(z_flag) = panic_mode.cargo_z_flag()
247    {
248        cmd.arg("-Z").arg(z_flag);
249    }
250
251    // Handle cargo config
252    let has_profile = spec.cargo.profile.is_some();
253    if let Some(ref profile) = spec.cargo.profile {
254        match profile {
255            Profile::Release => {
256                cmd.arg("--release");
257            }
258            Profile::Debug => {
259                // Debug is default, no flag needed
260            }
261        }
262    }
263
264    if let Some(ref triple) = spec.cargo.target_triple {
265        cmd.arg("--target").arg(triple);
266    }
267
268    if let Some(ref path) = spec.cargo.target_json {
269        cmd.arg("-Z").arg("json-target-spec");
270        cmd.arg("--target").arg(path);
271    }
272
273    for flag in &spec.cargo.unstable {
274        cmd.arg("-Z").arg(flag);
275    }
276
277    // Inject --config key=value pairs
278    for (key, value) in &spec.cargo.config_key_value {
279        cmd.arg("--config").arg(format!("{}={}", key, value));
280    }
281
282    // If no profile in spec but release flag passed, use release
283    if !has_profile && release {
284        cmd.arg("--release");
285    }
286
287    // Collect rustc flags
288    let mut rustc_flags: Vec<String> = Vec::new();
289
290    // Handle high-level panic mode (rustc -C flag)
291    if let Some(panic_mode) = spec.panic
292        && let Some(panic_value) = panic_mode.rustc_panic_value()
293    {
294        rustc_flags.push(format!("-C panic={}", panic_value));
295    }
296
297    // Handle rustc config
298    let rustc = &spec.rustc;
299
300    // Handle high-level strip mode
301    if let Some(strip_mode) = spec.strip
302        && let Some(strip_value) = strip_mode.rustc_strip_value()
303    {
304        rustc_flags.push(format!("-C strip={}", strip_value));
305    }
306
307    if !rustc.build_std.is_empty() {
308        // -Z build-std is a cargo flag, not rustc
309        let crates_str = rustc.build_std.join(",");
310        cmd.arg("-Z").arg(format!("build-std={}", crates_str));
311    }
312
313    for flag in &rustc.flags {
314        rustc_flags.push(flag.clone());
315    }
316
317    // Handle version script (generates file and adds linker arg)
318    if let Some(vs) = &spec.linker.version_script {
319        let vs_dir = match expanded_target_dir {
320            Some(td) => workspace.join("target").join(td),
321            None => workspace.join("target"),
322        };
323        let _ = fs::create_dir_all(&vs_dir);
324        let version_script_path = vs_dir.join("tspec-version.script");
325
326        // Generate version script: { global: sym1; sym2; local: *; };
327        let globals = vs.global.join("; ");
328        let content = format!("{{ global: {}; local: {}; }};", globals, vs.local);
329
330        let mut f =
331            fs::File::create(&version_script_path).context("failed to create version script")?;
332        writeln!(f, "{}", content)?;
333
334        rustc_flags.push(format!(
335            "-C link-arg=-Wl,--version-script={}",
336            version_script_path.display()
337        ));
338    }
339
340    // Apply rustc flags (linker args from Args handled by generated build.rs)
341    if !rustc_flags.is_empty() {
342        cmd.env("RUSTFLAGS", rustc_flags.join(" "));
343    }
344
345    Ok(())
346}
